mapper.select:
SELECT c.* FROM hjj.chat c JOIN ( SELECT MAX(id) AS max_id FROM hjj.chat WHERE isDelete = 0 AND (fromId = #{userId} OR toId = #{userId}) AND toId IN
#{friendId}
GROUP BY toId ) AS max_ids ON c.id = max_ids.max_id AND (c.fromId = #{userId} OR c.toId = #{userId}) order by createTime desc;
